                   WHEN 'PZ' THEN  64 -- must match oracle.dbtools.migration.workbench.plugin.Teradata13Plugin.PERIOD_LENGTH
                   WHEN 'SC' THEN  CASE DECIMALFRACTIONALDIGITS > 0
